<script setup>import { onMounted, onBeforeUnmount, inject } from 'vue';// Import step componentsimport CreationFlowStep1 from "./CreationFlowStep1.vue";// Inject the global stateconst globalCreationState = inject('globalCreationState');const createMyLogoIsActive = inject('createMyLogoIsActive');const advanceStep = () => {  globalCreationState.creationStep += 1;  console.log('Advancing to step:', globalCreationState.creationStep);};const previousStep = () => {  if (globalCreationState.creationStep > 1) {    globalCreationState.creationStep -= 1;    console.log('Going back to step:', globalCreationState.creationStep);  }};// Function to disable the back button by constantly pushing the same stateconst disableBackButton = () => {  history.pushState(null, document.title, window.location.href);};// Initial setup when component mountsonMounted(() => {  globalCreationState.creationStep = 1; // Ensure initial step is set to 1  console.log('Component mounted');  console.log('Initial creationStep:', globalCreationState.creationStep);  // Set variable boolean status for createMyLogo  createMyLogoIsActive.value = true;  // Optional: Add the popstate event listener if you want to disable back navigation  window.addEventListener('popstate', disableBackButton);});// Clean up when component is unmountedonBeforeUnmount(() => {  window.removeEventListener('popstate', disableBackButton);});</script><template>  <section class="cyborg-creation w-full relative">      <!-- Step 1 -->      <creation-flow-step1 v-if="globalCreationState.creationStep === 1"></creation-flow-step1>      <!-- INTERNAL DEV STUFF *** REMOVE FOR LAUNCH -->    <div class="w-full py-[30px] flex items-center justify-center">      <button class="bg-black text-white p-[10px]" @click="advanceStep">Next Step</button>      <button class="bg-black text-white p-[10px]" @click="previousStep">Previous Step</button>    </div>    <h1 class="block text-6xl font-black py-[100px]"> Current Step: {{ globalCreationState.creationStep }} </h1>  </section></template><style scoped lang="scss">/* Add your scoped styles here */</style>