<script setup>  import { ref, onMounted, computed } from "vue";  import FrequentlyAskedQuestions from "../components/homepage/FrequentlyAskedQuestions.vue";  import ContactSupportForm from "../components/homepage/ContactSupportForm.vue";  import Fuse from "fuse.js";  // set variables and refs  const progressStatus = ref(25);  const selectedIndustry = ref(null);  const query = ref("");  const results = ref([]);  const showDropdown = ref(false);  const industries = [    { id: 1, name: "Electrical" },    { id: 2, name: "Plumbing" },    { id: 2, name: "Construction" },    { id: 2, name: "General Contractor" },    { id: 2, name: "Landscaping" },  ];  // Fuse.js setup  const fuse = new Fuse(industries, {    keys: ["name"],    threshold: 0.3, // Adjust sensitivity (0.0 is very strict, 1.0 is very lenient)  });  const search = () => {    results.value = query.value ? fuse.search(query.value) : [];  };  const selectItem = (item) => {    query.value = item.name;    showDropdown.value = false;  };  // Hide dropdown on blur with a slight delay to allow selection  const hideDropdown = () => {    // Clear `query` if it doesn't match any industry in the list    const matchingIndustrySearchQuery = industries.find((industry) => industry.name.toLowerCase() === query.value.toLowerCase());    if (!matchingIndustrySearchQuery) {      query.value = ""; // Clear the field if there's no match    }    setTimeout(() => {      showDropdown.value = false;    }, 100);  };  // Computed property to check if input should be in "focused" state  const isFocused = computed(() => showDropdown.value || query.value.trim() !== "");</script><template>  <section class="creation-step step-1 w-full relative block border-t-2 border-t-neutral-200 font-primary">    <!-- step progress bar -->    <div class="progress-bar absolute block top-[-2px] left-0 right-0 h-[4px] w-full z-[1]">      <div :style="{ width: progressStatus + '%', maxWidth: progressStatus + '%' }" class="progress-bar-line bg-brand-200 h-full"></div>    </div>    <!-- Step One Content -->    <div class="step-content site-container pt-[34px]">      <h1 class="block relative text-black font-bold text-[20px] md:text-[26px] lg:text-[32px]leading-[1.2]"> Select your industry</h1>      <p class="block relative text-black font-normal text-[18px] md:text-[20px] lg:text-[24px] pt-[20px] leading-[1.35]">        This will help create a more custom look for your industry.      </p>      <!-- Industry Selection -->      <div class="search-industry relative w-full mt-[24px] md:mt-[35px] lg:mt-[50px]">        <div class="search-industry-input-field w-full relative block" :class="{ 'focused': isFocused }">          <label for="search-industry" class="absolute top-[22px] left-[18px] text-[12px] text-black font-normal transition transition-fast">            Select Specialty (e.g., Plumbing, Electrical)          </label>          <input              type="text"              v-model="query"              @input="search"              @focus="showDropdown = true"              @blur="hideDropdown"              name="search-industry"              :class="{ 'focused': isFocused }"              class="industry-search-input border-[3px] border-neutral-200 block w-full text-black font-medium min-h-[64px] max-h-[64px] px-[18px] transition transition-fast focus:border-brand-electric-blue active:border-brand-electric-blue"          />        </div>        <!-- Dropdown for search results -->        <ul v-if="showDropdown && results.length" class="dropdown-list absolute w-full border border-neutral-200 bg-white max-h-[200px] md:max-h-[290px] lg:max-h-[385px] overflow-y-auto z-10">          <li              v-for="result in results"              :key="result.item.id"              @mousedown.prevent="selectItem(result.item)"              class="dropdown-item text-black text-[16px] md:text-[18px] lg:text-[20px] font-medium lg:font-semibold py-[17px] px-[20px] lg:py-[12px] hover:text-brand-electric-blue hover:bg-neutral-100 cursor-pointer transition transition-fast"          >            {{ result.item.name }}          </li>        </ul>      </div>    </div>  </section>  <div class="bottom-content w-full relative lg:mt-[290px] hidden lg:block">    <frequently-asked-questions></frequently-asked-questions>    <contact-support-form></contact-support-form>  </div></template><style scoped lang="scss">  /* Optional styling for dropdown */  .dropdown-list {    @media only screen and (min-width: 1024px) {      box-shadow: 0 5px 12px 3px rgba(0,0,0, .10);    }  }  .dropdown-item:hover {    background-color: #e2e8f0; /* Replace with your brand color */  }  .industry-search-input {    transition: border-color 0.3s, box-shadow 0.3s; // Smooth transition for focus effect    &.focused {      padding-top: 32px;      padding-bottom: 17px;    }    &:focus,    &:active {      outline: none;      padding-top: 32px;      padding-bottom: 17px;    }  }  .search-industry-input-field {    position: relative;    &.focused label {      top: 10px; // Move label to the top when focused      transition: color 0.3s, top 0.3s;    }  }</style>