<script setup>import { defineProps } from "vue";// Declare Propsconst { addonClasses, altText, imageSource, lazyLoad } = defineProps({  addonClasses: {    type: String,    required: false,    default: '',  },  altText: {    type: String,    required: false,    default: '',  },  imageSource: {    type: String,    required: false,    default: '',  },  lazyLoad: {    type: Boolean,    required: false,    default: true,  }});</script><template>  <img v-if="lazyLoad"       :class="[addonClasses, { 'lazy': lazyLoad, 'lazyload': lazyLoad }]"       :src="imageSource" :alt="altText" loading="lazy"  />  <img      v-else      :class="addonClasses"      :src="imageSource"      :alt="altText"  /></template><style scoped lang="scss"></style>