<script setup>import {ref, onMounted, watch} from "vue";import ImageElement from "./ImageElement.vue";import {defineProps, provide } from "vue";const props = defineProps({  showPasswordScreen: {type: Boolean, default: true}});const password = ref("");const isAuthenticated = ref(false);onMounted(() => {  if (props.showPasswordScreen) {    isAuthenticated.value = localStorage.getItem("isAuthenticated") === "true";  } else {    isAuthenticated.value = true; // Skip password screen if disabled  }});const verifyPassword = (event) => {  event.preventDefault();  if (password.value === "Cyborg2024!") {    isAuthenticated.value = true;    localStorage.setItem("isAuthenticated", "true"); // Store state in localStorage  } else {    alert("Incorrect password");  }};// Watch for changes in showPasswordScreen to clear localStorage if neededwatch(    () => props.showPasswordScreen,    (newValue) => {      if (!newValue) {        localStorage.removeItem("isAuthenticated");        isAuthenticated.value = true;      }    });/* -------- Login Modal Popups -------- */// State to control modal visibilityconst loginModalVisible = ref(false);const signupModalVisible = ref(false);// Toggle function for the modalconst toggleLoginModal = () => {  loginModalVisible.value = !loginModalVisible.value;};const toggleSignupModal = () => {  signupModalVisible.value = !signupModalVisible.value;};// Provide the state and toggle function to child componentsprovide("signupModalVisible", signupModalVisible);provide("toggleSignupModal", toggleSignupModal);provide("loginModalVisible", loginModalVisible);provide("toggleLoginModal", toggleLoginModal);</script><template>  <section v-if="!isAuthenticated && showPasswordScreen"           class="password-screen w-full h-full min-h-screen min-w-screen z-50 bg-brand-200 flex flex-col justify-center">    <div class="site-container">      <div class="main-container w-full h-full relative flex flex-col justify-center items-center py-[50px]">        <strong class="text-black font-black text-[36px] lg:text-[48px] leading-[1] pb-[30px] uppercase">Password          Login</strong>        <form id="password-entry-form" class="w-full h-full relative max-w-[500px]" @submit="verifyPassword">          <div class="form-row w-full relative flex flex-row justify-center">            <label class="flex-0 hidden" for="password" hidden>password</label>            <input                v-model="password"                type="password"                name="password"                placeholder="Password"                class="block bg-white text-brand-100 text-base py-[22px] px-[30px] w-full mx-auto relative self-stretch"            />            <button type="submit"                    class="self-stretch max-w-fit ml-[5px] block bg-black text-white uppercase font-black text-[16px] text-center py-[20px] px-[25px]">              Submit            </button>          </div>        </form>      </div>    </div>  </section>  <!-- Main content of the app will be displayed here if authenticated -->  <div v-else class="main-content">    <slot/>  </div></template><style scoped lang="scss"></style>